import React, {Component} from "react"

class Form extends Component {
    constructor() {
        super()
        this.state = {
            Gender: "Man",
            Orientation: "Hetrosexual",
            Meeting: " "
        }
        this.clickHandler = this.clickHandler.bind(this)
        this.handleChange= this.handleChange.bind(this)
    }

    clickHandler(event) {
        this.setState({
            Gender: event,
            Orientation: event,
            Meeting: event,
            Name: ""
        })
    }

    handleChange(event) {
        const {Name} = event.target
    }

    render() {
        return (
            <form>
                <label>How do you identify yourself: </label>
                <button onClick={this.clickHandler}>Man</button>
                <button onClick={this.clickHandler}>Woman</button><br/>

                <label>Your sexual orientation: </label>
                <button onClick={this.clickHandler}>Hetrosexual</button>
                <button onClick={this.clickHandler}>Bisexual</button><br/>

                <label>I am open to meeting: </label>
                <button onClick={this.clickHandler}>Hetrosexual Woman</button>
                <button onClick={this.clickHandler}>Bisexual Woman</button>
                <button onClick={this.clickHandler}>Both</button><br/>

                <label>Display name</label>
                <input 
                    type='text' 
                    value={this.state.Name}
                    placeholder="Enter"
                    onChange={this.handleChange}
                    />
            </form>
        )
    }
}

export default Form
