        // })
    }

    render(){
        return(
            <div>
                <ToggleButtonGroup type="checkbox" defaultValue={[1]} className="mb-2">
                <ToggleButton value={1} onChange={this.handleChange}>Checkbox 1</ToggleButton>
                <ToggleButton value={2} onChange={this.handleChange}>Checkbox 2</ToggleButton>
                <ToggleButton value={3} onChange={this.handleChange}>Checkbox 3</ToggleButton>
                </ToggleButtonGroup>
                <br />
            </div>
        )
    }


}

export default Buttons
