import React, { Component } from 'react';
import ToggleButtonGroup from 'react-bootstrap/ToggleButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';
import DropdownButton from 'react-bootstrap/DropdownButton'
import Dropdown from 'react-bootstrap/Dropdown'

class Buttons extends Component {
    constructor(){
        super()
        this.state = {
            Gender: 1,
            Orientation: 1,
            Meeting: null,
            Name: "",
            Education: null,
            School: "Select",
            Occupation: null

        }  
        this.handleText=this.handleText.bind(this)
        this.handleGender=this.handleGender.bind(this)
        this.handleDropdown=this.handleDropdown.bind(this)
        this.handleMeeting=this.handleMeeting.bind(this)
        this.handleOrientation=this.handleOrientation.bind(this)
        this.handleEducation=this.handleEducation.bind(this)
    }
    //const [radioValue, setRadioValue] = useState('1');

    // sorter(event){
    //     var partialState = {};
    //     partialState[event.target.Gender] = event.target.value;
    //     this.setState(partialState);
    // }
    handleText(event) {
        const {Name, value2}= event.currentTarget
        this.setState({[Name]: value2})   

    }
    handleGender(event){
        // const {Gender, value} = event.currentTarget
        // this.setState({[event.currentTarget.GehandleDropdown(event) {
    //     // const {eventKey, value}= event.currentTarget
    //     // this.setState({[eventKey]: value})
    //     console.log(event)
    // }nder]: event.target.checked})
        //setRadioValue(event.currentTarget.value)
        const {Gender, value} = event.currentTarget
        this.setState({[Gender]: value})
    }
    handleOrientation(event) {
        const {Orientation, value} = event.currentTarget
        this.setState({[Orientation]: value})
    }
    handleMeeting(event) {
        const{Meeting, value}= event.currentTarget
        this.setState({[Meeting]: value})
    }
    handleEducation(event) {
        const{Education, value}= event.currentTarget
        this.setState({[Education]: value})
    }
    handleDropdown(event) {
        console.log(event)
        this.setState({School: "School " + event})
    }
    // output(event){
    //     event.preventDefault(),
    //     <h1>{this.state.Gender}</h1>
    // }
    render(){
        return(
            <form>
                <div class='row'>
                    <label class='label col-sm-4'>How do you identify yourself: </label>
                    <ToggleButtonGroup type="radio" defaultValue={[1]} className="mb-2 col-sm-4" name="Gender">
                    <ToggleButton value={1} onChange={this.handleGender}>Man</ToggleButton>
                    <ToggleButton value={2} onChange={this.handleGender}>Woman</ToggleButton>
                    </ToggleButtonGroup>
                </div>

                <br />
                <div class='row'>
                <label class='label col-sm-4'>Your sexual orientation: </label>
                <ToggleButtonGroup type="radio" defaultValue={[1]} className="mb-2 col-sm-6" name="Orientation">
                <ToggleButton value={1} onChange={this.handleOrientation}>Hetrosexual</ToggleButton>
                <ToggleButton value={2} onChange={this.handleOrientation}>Bisexual</ToggleButton>
                </ToggleButtonGroup>
                </div>

                <br />

                <div class='row'>
                <label class='label col-sm-4'>I am open to meeting: </label>
                <ToggleButtonGroup type="radio" className="mb-2 col-sm-6" name="meeting">
                <ToggleButton value={1} onChange={this.handleMeeting}>Hetrosexual Woman</ToggleButton>
                <ToggleButton value={2} onChange={this.handleMeeting}>Bisexual Woman</ToggleButton>
                <ToggleButton value={3} onChange={this.handleMeeting}>Both</ToggleButton>
                </ToggleButtonGroup>
                </div>

                <br />


                <label class='label col-sm-4'>Display name: </label>
                <input 
                    type='text' 
                    value2={this.state.Name}
                    placeholder="Enter"
                    onChange={this.handleText}
                />
                <br/>
                <div class='form-grp row'>
                    <label class='col-sm-4'>Education/Qualification: </label>
                    <ToggleButtonGroup type="radio" defaultValue={[1]} className="mb-2 col-sm-6" name="edu">
                    <ToggleButton value={1} onChange={this.handleEducation}>High School</ToggleButton>
                    <ToggleButton value={2} onChange={this.handleEducation}>Bachelor</ToggleButton>
                    <ToggleButton value={3} onChange={this.handleEducation}>Master</ToggleButton>
                    <ToggleButton value={4} onChange={this.handleEducation}>Doctorate</ToggleButton>
                    <ToggleButton value={5} onChange={this.handleEducation}>PG Diploma</ToggleButton>
                    </ToggleButtonGroup>
                </div>
                <br />
                <div class='row'>
                    <label class='col-sm-4'>University/School: </label>
                    <DropdownButton id="dropdown-item-button" title={this.state.School} className='col-sm-6'>
                    <Dropdown.ItemText>Dropdown item text</Dropdown.ItemText>
                    <Dropdown.Item eventKey="1" onSelect = {this.handleDropdown}>School 1</Dropdown.Item>
                    <Dropdown.Item eventKey="2" onSelect = {this.handleDropdown}>School 2</Dropdown.Item>
                    <Dropdown.Item eventKey="3" onSelect = {this.handleDropdown}>School 3</Dropdown.Item>
                    </DropdownButton>
                </div>
                <br />
                <div class='row'> 
                <label class="btn-group col-sm-4">Profession/Occupation: </label>
                    <DropdownButton id="dropdown-item-button" title="Select" className='col-sm-6'>
                    <Dropdown.ItemText>Dropdown item text</Dropdown.ItemText>
                    <Dropdown.Item eventKey="1" onChange={this.handleDropdown}>Pro 1</Dropdown.Item>
                    <Dropdown.Item eventKey="2" onChange={this.handleDropdown}>Pro 2</Dropdown.Item>
                    <Dropdown.Item eventKey="3" onChange={this.handleDropdown}>Pro 3</Dropdown.Item>
                    </DropdownButton>
                </div>

                <br/>
            </form>
        )
    }


}

export default Buttons
